# -*- coding: utf-8 -*-"""Created on Sun Sep 18 15:14:00 2022@author: surajgiriProblem 2"""# Future value after saving for 480 months = Present value before spending for 360 monthsdef main():        # Present value befoire starting the withdrawl    PV = 2000 * ((1+(0.02/12))**(-360) - 1)/(1-(1+(0.02/12)))    # print(PV)        # After Equating Future Value after saving for 480 months with PV    A = ((PV / 12) * 0.02 ) / (((1 + (0.02/12))**480)-1)    print("The amount saved monthly is: ", A)if __name__ == "__main__":    main()